import React, { useState, useEffect } from 'react'
import './App.css';
const FetchComponent = () => {
  const [datas, setData] = useState({});
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const getData = async () => {
      setIsLoading(true);
      const target = await fetch("https://api.weatherapi.com/v1/current.json?key=da7e22d2b34d48f6a39124748231607&q=nepal&aqi=no")
      const value = await target.json();
      setData(value)
      setIsLoading(false)
    }
    getData()
  }, []);


  return (
    <>
    {!isLoading && datas ? <h1>{datas.current_weather}</h1>:<h1>error</h1>}
    </>
  )
  }

export default FetchComponent
