import './App.css';
import React from 'react';

class PrintName extends React.Component {
  constructor(props) {
    super(props) 
    this.state = {
      users: [
        {name: "bigyan", profession:"podey"},
        {name: "pratik", profession: "netwrok engineer"},
        {name: "shoyub", profession: "VFX"},
        {name: "riwaj", profession: "manager"}
      ],
      name: this.props.name
    }
  }
  render() {
    return (
      <>
      {this.state.users.map((user) => {
        if(user.name == this.state.name) {
       return  <div key={user.name}>
       <h1 className='demo'>{user.name}</h1>
       <li>{user.profession}</li>
       </div>
        }
        else 
        return <div key={user.name}>
        <h1>{user.name}</h1>
        <li>{user.profession}</li>
        </div>
      })}

      </>
    )
  }
}

export default PrintName;
