import React, { useState, useEffect } from 'react'
import './App.css';
const FetchComponent = () => {
  const [datas, setData] = useState({});
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const getData = async () => {
      setIsLoading(true);
      const target = await fetch("https://api.open-meteo.com/v1/forecast?latitude=52.52&longitude=13.41&current_weather=true&hourly=temperature_2m,relativehumidity_2m,windspeed_10m")
      const value = await target.json();
      setData(value)
      setIsLoading(false)
    }
    getData()
  }, []);

  if(!isLoading && datas) {
  return (
    <>
    <div>
      <h1>{datas.current_weather.temperature}</h1>
    </div>
    </>
  )
  }
}
export default FetchComponent
