import './App.css';
import React from 'react';

class PrintName extends React.Component {

  constructor(props) {
    super(props) 
    this.state = {
     /* users: [
        {name: "bigyan", profession:"podey"},
        {name: "pratik", profession: "netwrok engineer"},
        {name: "shoyub", profession: "VFX"},
        {name: "riwaj", profession: "manager"}
      ],
      */
      name: this.props.name
    }
    this.style = {
      fontSize: "126px",
      borderTop: "30px",
      TextTransform: "uppercase"
    }
  }
  render() {
    return (
      <>
      <h1 style={this.style} className={this.state.name == "bigyan" ? "demo" : ""}>{this.state.name}</h1>

      </>
    )
  }
}

export default PrintName;
